#import <Foundation/Foundation.h>
#import <UIKit/UIKit.h>
#import "PingPongPresenter.h"
#import "PingPongProtocols.h"

@interface PingPongPresenter () <PingPongPresenterInterface>
@property (strong, nonatomic) NSObject<PingPongWireframeInterface>* router;
@end

@implementation PingPongPresenter

- (instancetype)initWithView:(NSObject<PingPongView>*)view router:(NSObject<PingPongWireframeInterface>*)router {
    self = [super init];
    if (self) {
        _view = view;
        _router = router;
    }
    return self;
}

- (void)viewDidAppear {
    //Default implementation
}

- (void)viewDidDissappear {
    //Default implementation
}

- (void)viewDidLoad {
    [self.view displayMenu];
    [self.view displayGameOverlay];
}

- (void)viewWillAppear {
    [self.view displayMenu];
    [self.view displayGameOverlay];
}

- (void)viewWillDissappear {
    //Default implementation
}

- (void)didSelectSettings {
    [self.view displayMenu];
    [self.view hideGameOverlay];
    [self.view displaySettings];
}


- (void)didSelectStartGame {
    [self.view hideMenu];
    
}

- (void)didSelectMenuButton {
    [self.view pauseGame];
    [self.view displayMenu];
    [self.view hideSettings];
    [self.view displayGameOverlay];
}



@end
